export default function({ items, name, currency, createdAt }) {
  const jsPDF = require('jspdf');
  const doc = new jsPDF();

  doc.setFontSize(14);
  doc.text('```', 20, 20);

  doc.setFontSize(18);
  doc.text(`[INV-${name.yearIssued}-${name.sfx}]`, 20, 30);

  doc.setFontSize(14);
  doc.text('----------------------------------', 20, 40);

  doc.setFontSize(12);
  doc.text(`Created on: ${createdAt}`, 20, 50);

  doc.setFontSize(16);
  items.forEach(item => {
    doc.text(`Item - ${item.description}      ${currency}${item.price}`);
  });

  doc.setFontSize(14);
  doc.text('----------------------------------', 20, 70);

  doc.setFontSize(12);
  doc.text(`TOTAL: todo...`, 20, 80);

  doc.setFontSize(16);
  doc.text('```', 20, 90);

  doc.save(`INV-${name.yearIssued}-${name.sfx}.pdf`);
}
