import React from 'react';
import { Mutation } from 'react-apollo';
import { UPDATE_INVOICE_MUTATION } from './UpdateInvoice';
import styled from 'styled-components';

const HiddenCheckbox = styled.input.attrs({ type: 'checkbox' })`
  border: 0;
  clip: rect(0 0 0 0);
  clippath: inset(50%);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  white-space: nowrap;
  width: 1px;
  ${HiddenCheckbox}:focus + & {
    box-shadow: 0 0 0 3px pink;
  }
`;

const StyledCheckbox = styled.div`
  border-radius: 3px;
  transition: all 150ms;
  display: inline-block;
  width: 16px;
  height: 16px;
  background: ${props => (props.checked ? 'salmon' : 'papayawhip')};
`;

const CheckboxContainer = styled.div`
  display: inline-block;
  vertical-align: middle;
  cursor: pointer;
`;

const TogglePaid = ({ vars }) => (
  <Mutation mutation={UPDATE_INVOICE_MUTATION} variables={{ ...vars, isPaid: !vars.isPaid }}>
    {(updateInvoice, { error, loading }) => {
      return (
        <CheckboxContainer>
          <HiddenCheckbox
            type="checkbox"
            defaultChecked={vars.isPaid}
            onClick={async () => {
              await updateInvoice();
            }}
          />
          <StyledCheckbox />
        </CheckboxContainer>
      );
    }}
  </Mutation>
);

export default TogglePaid;
