import React, { Component } from 'react';
import { Mutation } from 'react-apollo';
import gql from 'graphql-tag';
import { QUERY_ALL_INVOICES } from './InvoiceList';
import { CURRENT_INVOICE_QUERY } from './Invoice';
import { DeleteButton } from './styles/Button';
import styled from 'styled-components';

const DELETE_ITEM_MUTATION = gql`
  mutation DELETE_ITEM_MUTATION($id: ID!) {
    deleteItem(id: $id) {
      id
    }
  }
`;

const DeleteItemBtn = styled.div`
  width: auto;
  color: black;
  border: 0;
  font-size: 1.2rem;
  font-weight: 600;
  padding: 0.3rem 0.4rem;
  height: 3rem;
  cursor: pointer;
`;

class DeleteItem extends Component {
  // APOLLO MANUAL UPDATE AFTER MUTATION
  // update = (cache, payload) => {
  //   //Manually update the cache on the client, so it matches the server
  //   //read cache for the items we want
  //   const data = cache.readQuery({ query: QUERY_ALL_INVOICES });
  //   //filter the deleted item out
  //   data.invoices = data.invoices.filter(invoice => invoice.id !== payload.data.deleteInvoice.id);
  //   //put the items back
  //   cache.writeQuery({ query: QUERY_ALL_INVOICES, data });
  // };

  render() {
    return (
      <Mutation mutation={DELETE_ITEM_MUTATION} variables={{ id: this.props.id }} >
        {deleteItem => (
          <DeleteItemBtn
            onClick={() => {
              if (confirm('Are you sure you want to delete this item?')) {
                deleteItem().catch(err => {
                  alert(err.message);
                });
              }
            }}
          >
            {this.props.children}
          </DeleteItemBtn>
        )}
      </Mutation>
    );
  }
}

export default DeleteItem;
