import styled from 'styled-components';

const SelectStyles = styled.select`
  width: 9rem;
  height: 3rem;
  background: white;
  color: gray;
  padding-left: 5px;
  font-size: 1.6rem;
  border: none;
  margin: 1.3rem 0.2rem 0 0;
  cursor: pointer;
  &:hover{
    color: black;
  }
  option {
    color: black;
    background: white;
    display: flex;
    white-space: pre;
    min-height: 20px;
    padding: 0px 2px 1px;
  }
`;

export default SelectStyles;
