import React from 'react';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import styled from 'styled-components';
import Pagination from './Pagination';
import InvoiceListItem from './InvoiceListItem';
import { perPage } from '../config';


const QUERY_ALL_INVOICES = gql`
  query QUERY_ALL_INVOICES($skip: Int = 0, $first: Int = ${perPage}, $currency: String, $status: String) {
    invoices(orderBy: createdAt_DESC, first: $first, skip: $skip, where: { currency: $currency , status: $status }) {
      id
      status
      currency
      isPaid
      createdAt
      name {
        id
        sfx
        yearIssued
      }
      items {
        id
        description
        price
      }
    }
  }
`;

const Center = styled.div`
  text-align: center;
`;


const StyledList = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

class InvoiceList extends React.Component {


  render() {
    return (
      <Center>
        <Pagination page={this.props.page} />
        <Query
          query={QUERY_ALL_INVOICES}
          variables={{
            where: { status: this.props.status, currency: this.props.currency },
            skip: this.props.page * perPage - perPage
          }}
        >
          {({ data, error, loading, refetch }) => {
            console.log('INVOICE LIST: ', data.invoices);
            if (loading) return <p>Loading...</p>;
            if (error) return <Error error={error} />;

            this.refetch = refetch;

            return (
              <StyledList>
                {data.invoices.map(invoice => (
                  <InvoiceListItem key={invoice.id} invoice={invoice} />
                ))}
              </StyledList>
            );
          }}
        </Query>
        <Pagination page={this.props.page} />
      </Center>
    );
  }
}

export default InvoiceList;
export { QUERY_ALL_INVOICES };
