import React from 'react';
import { Mutation } from 'react-apollo';
import { UPDATE_INVOICE_MUTATION } from './UpdateInvoice';

const TogglePaid = ({ vars }) => (
  <Mutation mutation={UPDATE_INVOICE_MUTATION} variables={{ isPaid: !vars.isPaid }}>
    {(updateInvoice, { error, loading }) => {
      return (
        <input
          type="checkbox"
          defaultChecked={vars.isPaid}
          onClick={async () => {
            const data = await updateInvoice();
            console.log("Toggled paid flag!", data);

          }}
        />
      );
    }}
  </Mutation>
);

export default TogglePaid;
