import React from 'react';
import { Mutation } from 'react-apollo';
import { UPDATE_INVOICE_MUTATION } from './UpdateInvoice';
import styled from 'styled-components';

const HiddenCheckbox = styled.input.attrs({ type: 'checkbox' })`
  border: 0;
  clip: rect(0 0 0 0);
  clippath: inset(50%);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  white-space: nowrap;
  width: 1px;
`;

const StyledCheckbox = styled.div`
  border-radius: 3px;
  transition: all 150ms;
  display: inline-block;
  width: 16px;
  height: 16px;
  cursor: pointer;
  background: ${props => (props.checked ? props.blue : props.lightgrey)};
  ${HiddenCheckbox}:focus + & {
    box-shadow: 0 0 0 3px black;
  }

  ${Icon} {
    visibility: ${props => (props.checked ? 'visible' : 'hidden')};
  }
`;

const CheckboxContainer = styled.div`
  display: inline-block;
  vertical-align: middle;
`;

const Icon = styled.svg`
  fill: none;
  stroke: white;
  stroke-width: 2px;
`;

const TogglePaid = ({ vars, checked }) => (
  <Mutation mutation={UPDATE_INVOICE_MUTATION} variables={{ ...vars, isPaid: !vars.isPaid }}>
    {(updateInvoice, { error, loading }) => {
      return (
        <CheckboxContainer>
          <HiddenCheckbox
            type="checkbox"
            defaultChecked={checked}
            onClick={async () => {
              await updateInvoice();
            }}
          />
          <StyledCheckbox defaultChecked={checked}>
            <Icon viewBox="0 0 24 24">
              <polyline points="20 6 9 17 4 12" />
            </Icon>
          </StyledCheckbox>
        </CheckboxContainer>
      );
    }}
  </Mutation>
);

export default TogglePaid;
