import moment from 'moment';

export default function({ items, name, currency, createdAt }) {
  const jsPDF = require('jspdf');
  const doc = new jsPDF();
  const totalPrice = 0;

  let text = [
    '```',
    `[INV-${name.yearIssued}-${name.sfx}]`,
    '----------------------------------',
    `Created on: ${moment(createdAt).format('MMMM Do YYYY')}`,
    '----------------------------------',
    `TOTAL: ${totalPrice}`,
    '```'
  ];

  // function yCoordinate(items) {
  //   const res = 0;
  //   items.forEach(item => {
  //     res += 10;
  //     totalPrice = +item.price;
  //   });
  //   return res;
  // }
  items.forEach(item => {
    totalPrice = +item.price;
    text.splice(4, 0, `Item - ${item.description}      ${currency}${item.price}`);
  });

  doc.setFontSize(14);
  doc.text(text, 20, 20);

  // doc.text('```', 20, 20);

  // doc.setFontSize(18);
  // doc.text(`[INV-${name.yearIssued}-${name.sfx}]`, 20, 30);

  // doc.setFontSize(14);
  // doc.text('----------------------------------', 20, 40);

  // doc.setFontSize(12);
  // doc.text(`Created on: ${moment(createdAt).format('MMMM Do YYYY')}`, 20, 50);

  // doc.setFontSize(16);
  // items.forEach(item => {
  //   doc.text(`Item - ${item.description}      ${currency}${item.price}`);
  // });

  // doc.setFontSize(14);
  // doc.text('----------------------------------', 20, 70);

  // doc.setFontSize(12);
  // doc.text(`TOTAL: ${totalPrice}`, 20, 80);

  // doc.setFontSize(16);
  // doc.text('```', 20, 90);

  doc.save(`INV-${name.yearIssued}-${name.sfx}.pdf`);
}
