import styled from 'styled-components';

const SelectStyles = styled.div`
  position: relative;
  display: inline-block;
  margin: 1.5rem 0.2rem;
  padding: 0.5rem;
  font-size: 1rem;
  width: 8rem;
  select {
    display: inline-block;
    width: 100%;
    cursor: pointer;
    outline: 0;
    border: 0;
    border-radius: 0;
    background-color: ${props => props.theme.offWhite};
    color: ${props => props.theme.grey};
    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;

    &::-ms-expand {
      display: none;
    }
    &:hover,
    &:focus {
      color: ${props => props.theme.blue};
    }
  }
  div {
    position: absolute;
    top: 1.1rem;
    right: 1rem;
    width: 0;
    height: 0;
    pointer-events: none;
    border-style: solid;
    border-width: 8px 5px 0 5px;
    border-color: ${props => props.theme.grey} transparent transparent transparent;
    select{
      &:hover {
        border-top-color: black;
      }
      &:focus {
        border-top-color: black;
      }
    }
  }
`;

export default SelectStyles;
