import React from 'react';
import { Mutation } from 'react-apollo';
import gql from 'graphql-tag';
import { CreditButton } from './styles/Button';
import { Toggle } from './TogglePaid';

const CREDIT_NOTE_MUTATION = gql`
  mutation CREDIT_NOTE_MUTATION($descriptions: String!, $prices: Float!, $invoiceId: ID!) {
    createItem(descriptions: $descriptions, prices: $prices, invoiceId: $invoiceId) {
      id
      descriptions
      prices
    }
  }
`;

const CreditNote = ({ invoice, children }) => {

  return (
    <Mutation mutation={CREDIT_NOTE_MUTATION} variables={invoice}>
      {(updateInvoice, { error, loading }) => {
        return (
          <Toggle
            disabled={invoice.status === 'CREDITED' || loading}
            onClick={async () => {
              await updateInvoice();
            }}
          >
            {children}
          </Toggle>
        );
      }}
    </Mutation>
  );
};

export default CreditNote;
