import React from 'react';
import { Mutation } from 'react-apollo';
import { UPDATE_INVOICE_MUTATION } from './UpdateInvoice';
import styled from 'styled-components';

const Label = styled.label`
  /* Container */
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 12px;
  cursor: pointer;
  font-size: 22px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  /* Hide default checkbox */
  input {
    position: absolute;
    opacity: 0;
    cursor: pointer;
    height: 0;
    width: 0;
  }
  input:checked {
    background-color: #2196f3;
    &:after {
      display: block;
    }
  }
  span {
    &:after {
      left: 9px;
      top: 5px;
      width: 5px;
      height: 10px;
      border: solid white;
      border-width: 0 3px 3px 0;
      -webkit-transform: rotate(45deg);
      -ms-transform: rotate(45deg);
      transform: rotate(45deg);
    }
  }
`;

const Checkbox = styled.span`
  /* Custom checkbox */
  position: absolute;
  top: 0;
  left: 0;
  height: 25px;
  width: 25px;
  background-color: #eee;
  &:hover {
    background-color: #ccc;
  }
  &:after {
    content: '';
    position: absolute;
    display: none;
  }
`;

const TogglePaid = ({ vars }) => (
  <Mutation mutation={UPDATE_INVOICE_MUTATION} variables={{ ...vars, isPaid: !vars.isPaid }}>
    {(updateInvoice, { error, loading }) => {
      return (
        <Label>
          <input
            type="checkbox"
            defaultChecked={vars.isPaid}
            onClick={async () => {
              await updateInvoice();
            }}
          />
          <Checkbox />
        </Label>
      );
    }}
  </Mutation>
);

export default TogglePaid;
