import React, { Component } from 'react';
import styled from 'styled-components';
import Error from './ErrorMessage';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import moment from 'moment';
import { UpdateButton } from './styles/Button';
import DeleteInvoice from './DeleteInvoice';
import CreateItem from './CreateItem';
import ItemList from './ItemList';
import UpdateInvoice from './UpdateInvoice';
import generatePdf from '../lib/GeneratePdf';
import Button from './styles/Button';
import InvoicePreview from './styles/Invoice';
import TotalPrice from '../lib/CalcPrice';

const CURRENT_INVOICE_QUERY = gql`
  query CURRENT_INVOICE_QUERY($id: ID!) {
    invoice(where: { id: $id }) {
      id
      name {
        id
        sfx
        yearIssued
      }
      items {
        id
        description
        price
      }
      currency
      status
      isPaid
      createdAt
    }
  }
`;

const StyledInvoice = styled.div`
  margin: 5px;
  padding: 5px;
  display: flex;
  align-items: flex-start;
  justify-content: space-around;
`;

class Invoice extends Component {
  state = {
    showUpdateForm: false,
    showCreateItemForm: true,
    totalPrice: 0
  };

  render() {
    const id = this.props.id,
      { showUpdateForm: showUpdate, showCreateItemForm, totalPrice } = this.state;

    return (
      <Query query={CURRENT_INVOICE_QUERY} variables={{ id }}>
        {({ data, loading, error }) => {
          if (loading) return <p>Loading data...</p>;
          if (error) return <Error error={error} />;
          const { id, name, items, currency, status, createdAt } = data.invoice;

          return (
            <StyledInvoice>
              <div>
                <p>
                  Invoice INV-{name.yearIssued}-{name.sfx}
                </p>

                <UpdateButton
                  onClick={() => {
                    this.setState(() => ({ showUpdateForm: !showUpdate }));
                  }}
                >
                  {!showUpdate ? 'Update' : 'Cancel'}
                </UpdateButton>

                <DeleteInvoice id={data.invoice.id}>Remove</DeleteInvoice>

                {showCreateItemForm && <CreateItem invoice={data.invoice} />}
                {showUpdate && <UpdateInvoice invoice={data.invoice} />}
              </div>
              <InvoicePreview>
                {' '}
                {/* Invoice layout preview */}
                <button
                  onClick={() => {
                    generatePdf(data.invoice);
                    console.log('Generated invoice');
                  }}
                  disabled={status === 'CREDITED'}
                >
                  Generate PDF
                </button>
                <div>
                  ` ` `<br />
                  [INV-{name.yearIssued}-{name.sfx}]<br />
                  -----------------------------
                  <br />
                  Created on: {moment(createdAt).format('MMMM Do YYYY')}
                  <br />
                  {items.length > 0 ? (
                    <>
                      <ItemList items={items} invoiceId={id} />
                      <br />
                    </>
                  ) : (
                    <p>No items added.</p>
                  )}
                  -----------------------------
                  <br />

                  <p>TOTAL: {TotalPrice(items)}{currency}</p>
                  <br />` ` `
                </div>
              </InvoicePreview>
            </StyledInvoice>
          );
        }}
      </Query>
    );
  }
}

export default Invoice;
export { CURRENT_INVOICE_QUERY };
