import React, { Component } from 'react';
import styled from 'styled-components';
import { Query } from 'react-apollo';
import { LOCAL_STATE_QUERY } from './Header';
import InvoiceList from './InvoiceList';
import CreateInvoice from './CreateInvoice';
import Search from './Search';

const HomeStyles = styled.div`
  font-size: 14px;
  display: flex;
  flex-direction: column;
`;

const FilterStyles = styled.div`
  display: flex;
  justify-content: center;
  select {
    width: 10rem;
    padding: 0.5rem;
    font-size: 1rem;
    margin: 2rem;
    border: 1px solid black;
    &:focus {
      outline: 0;
      border-color: ${props => props.theme.blue};
    }
  }
`;
class Home extends Component {
  state = {
    filterBy: '',
    currency: 'EUR'
  };

  filterByCurrencyHandler = e => {
    const value = e.target.value;
    this.setState(() => ({
      currency: value
    }));
  };

  render() {
    const { currency } = this.state;
    return (
      <HomeStyles>
        <Search />
        <FilterStyles>
          <select name="currency" value={currency} onChange={this.filterByCurrencyHandler}>
            <option value="usd">$ (USD)</option>
            <option value="eur">€ (EUR)</option>
            <option value="cad">$‎ (CAD)</option>
            <option value="jpy">¥‎ (JPY)</option>
          </select>
        </FilterStyles>
        <Query query={LOCAL_STATE_QUERY}>
          {({ data }) => {
            return data.showCreateForm ? <CreateInvoice /> : <InvoiceList page={this.props.page} />;
          }}
        </Query>
      </HomeStyles>
    );
  }
}

export default Home;
