import React from 'react';
import moment from 'moment';
import ItemList from './ItemList';
import TotalPrice from '../lib/CalcPrice';

const InvoicePreview = ({ invoice }) => {
  return (
    <div>
      ` ` `<br />
      [INV-{invoice.name.yearIssued}-{invoice.name.sfx}]<br />
      -----------------------------
      <br />
      Created on: {moment(invoice.createdAt).format('MMMM Do YYYY')}
      <br />
      {invoice.items.length > 0 ? (
        <>
          <ItemList items={invoice.items} invoiceId={invoice.id} />
          <br />
        </>
      ) : (
        <p>No items added.</p>
      )}
      -----------------------------
      <br />
      <p>
        TOTAL: {TotalPrice(invoice.items)}
        {invoice.currency}
      </p>
      <br />` ` `
    </div>
  );
};

export default InvoicePreview;
