import React, { Component } from 'react';
import styled from 'styled-components';
import { Query } from 'react-apollo';
import { LOCAL_STATE_QUERY } from './Header';
import InvoiceList, { QUERY_ALL_INVOICES } from './InvoiceList';
import InvoiceListItem from './InvoiceListItem';
import CreateInvoice from './CreateInvoice';
import Search from './Search';
import Select from './styles/Select';
import Filter from './Filter';
import { perPage } from '../config';
import { ConfirmButton } from './styles/Button';

const HomeStyles = styled.div`
  font-size: 14px;
  display: flex;
  flex-direction: column;
`;

const StyledList = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

class Home extends Component {
  state = {
    currency: '',
    status: ''
  };

  filtersHandler = e => {
    const { name, value } = e.target;
    this.setState(() => ({ [name]: value }));
  };

  update = (currency, status) => {
    QUERY_ALL_INVOICES({
      variables: {
        currency,
        status
      }
    });
  };

  render() {
    const { currency, status } = this.state;
    const setRefetch = refetch => {
      this.refetch = refetch;
    };

    return (
      <HomeStyles>
        <Search />
        <Filter>
          <Select name="currency" value={currency} onChange={this.filtersHandler}>
            <option value="" hidden>
              currency
            </option>
            <option value="usd">$ (USD)</option>
            <option value="eur">€ (EUR)</option>
            <option value="cad">$‎ (CAD)</option>
            <option value="jpy">¥‎ (JPY)</option>
          </Select>
          <Select name="status" value={status} onChange={this.filtersHandler}>
            <option value="" hidden>
              status
            </option>
            <option value="draft">draft</option>
            <option value="real">real</option>
            <option value="credited">credited</option>
          </Select>
          <ConfirmButton onClick={this.update(currency, status)}>Apply filters</ConfirmButton>
        </Filter>
        <Query query={LOCAL_STATE_QUERY}>
          {({ data }) => {
            return data.showCreateForm ? (
              <CreateInvoice />
            ) : (
              <InvoiceList page={this.props.page}>
                <Query
                  query={QUERY_ALL_INVOICES}
                  variables={{
                    where: { currency: currency, status: status },
                    skip: this.props.page * perPage - perPage
                  }}
                >
                  {({ data, error, loading, refetch }) => {
                    console.log('INVOICE LIST: ', data.invoices);
                    if (loading) return <p>Loading...</p>;
                    if (error) return <Error error={error} />;
                    setRefetch(refetch);
                    return (
                      <StyledList>
                        {data.invoices.map(invoice => (
                          <InvoiceListItem key={invoice.id} invoice={invoice} />
                        ))}
                      </StyledList>
                    );
                  }}
                </Query>
              </InvoiceList>
            );
          }}
        </Query>
      </HomeStyles>
    );
  }
}

export default Home;
