import React from 'react';
import moment from 'moment';
import InvoicePreviewStyles from './styles/Invoice';
import ItemList from './ItemList';
import generatePdf from '../lib/GeneratePdf';
import TotalPrice from '../lib/CalcPrice';

const InvoicePreview = ({ invoice: { id, name, items, currency, status, createdAt } }) => {
  return (
    <InvoicePreviewStyles>
      {' '}
      {/* Invoice layout preview */}
      <button
        onClick={() => {
          generatePdf(invoice);
          console.log('Generated invoice');
        }}
        disabled={status === 'CREDITED'}
      >
        Generate PDF
      </button>
      <div>
        ` ` `<br />
        [INV-{name.yearIssued}-{name.sfx}]<br />
        -----------------------------
        <br />
        Created on: {moment(createdAt).format('MMMM Do YYYY')}
        <br />
        {items.length > 0 ? (
          <>
            <ItemList items={items} invoiceId={id} />
            <br />
          </>
        ) : (
          <p>No items added.</p>
        )}
        -----------------------------
        <br />
        <p>
          TOTAL: {TotalPrice(items)}
          {currency}
        </p>
        <br />` ` `
      </div>
    </InvoicePreviewStyles>
  );
};

export default InvoicePreview;
