import React from 'react';
import { Mutation } from 'react-apollo';
import gql from 'graphql-tag';
import { CreditButton } from './styles/Button';
import { Toggle } from './TogglePaid';

const CREDIT_NOTE_MUTATION = gql`
  mutation CREDIT_NOTE_MUTATION($description: String!, $price: Float!, $invoiceId: ID!) {
    createItem(description: $description, price: $price, invoiceId: $invoiceId) {
      id
      description
      price
    }
  }
`;

const CreditNote = ({ invoice }) => (
  <Mutation mutation={CREDIT_NOTE_MUTATION} variables={invoice}>
    {(updateInvoice, { error, loading }) => {
      return (
        <Toggle
          disabled={props.status === 'CREDITED' || loading}
          onClick={async () => {
            await updateInvoice();
          }}
        >
          {props.children}
        </Toggle>
      );
    }}
  </Mutation>
);

export default CreditNote;
