import React from 'react';
import { Mutation } from 'react-apollo';
import { UPDATE_INVOICE_MUTATION } from './UpdateInvoice';
import { CreditButton } from './styles/Button';

const CreditInvoice = (props) => (
  <Mutation mutation={UPDATE_INVOICE_MUTATION} variables={{ status: 'CREDITED' }}>
    {(updateInvoice, { error, loading }) => {
      return (
        <CreditButton
          disabled={props.status === "CREDITED"}
          onClick={async () => {
            await updateInvoice();
          }}
        >
          {props.children}
        </CreditButton>
      );
    }}
  </Mutation>
);

export default CreditInvoice;
