import React from 'react';
import InvoiceListItem from './InvoiceListItem';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import styled from 'styled-components';
import Pagination from './Pagination';
import { perPage } from '../config';

const StyledList = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const QUERY_ALL_INVOICES = gql`
  query QUERY_ALL_INVOICES($skip: Int = 0, $first: Int = ${perPage}) {
    invoices(orderBy: createdAt_DESC, first: $first, skip: $skip) {
      id
      status
      currency
      isPaid
      createdAt
      name {
        id
        sfx
        yearIssued
      }
      items {
        id
        description
        price
      }
    }
  }
`;

const Center = styled.div`
  text-align: center;
`;

const InvoiceList = (props) => (
  <Center>
    <Pagination page={props.page} />
    <Query query={QUERY_ALL_INVOICES} variables={{
      skip: props.page * perPage - perPage
    }}>
      {({ data, error, loading }) => {
        console.log('INVOICE LIST: ', data.invoices);
        if (loading) return <p>Loading...</p>;
        if (error) return <Error error={error} />;
        return (
          <StyledList>
            {data.invoices.map(invoice => (
              <InvoiceListItem key={invoice.id} invoice={invoice} />
            ))}
          </StyledList>
        );
      }}
    </Query>
    <Pagination page={props.page} />
  </Center>
);

export default InvoiceList;
export { QUERY_ALL_INVOICES };
